for filename in dfs.*; do echo $filename; echo ""; ret=$(grep -rn -v 'tty\|redundant\|SIGTERM\|retry.RetryInvocationHandler\|ConnectException\|RetriableException\|SafeModeException\|newSafemodeException\|EOFException\|wrapException\|BlockPlacementPolicy\|InterruptedException\|BlockStoragePolicy\|offerService\|peer\|ExitCodeException' $filename | grep "ERROR\|failed\|Failed\|Exception\|exception"); if [ "$ret" ]; then mv $filename to_check; fi; done; 
